TITLE: Configuring Directus SDK and Fetching Page Blocks
DESCRIPTION: TypeScript implementation for initializing the Directus client and creating a function to fetch page blocks by slug with nested relationships.
SOURCE: https://github.com/directus/docs/blob/main/content/tutorials/1.getting-started/rendering-dynamic-blocks-using-astro.md#2025-04-09_snippet_3

LANGUAGE: ts
CODE:
```
/// <reference types="vite/client" />
import { createDirectus, rest, readItems } from "@directus/sdk";

const DIRECTUS_URL = import.meta.env.DIRECTUS_URL;

const client = createDirectus(DIRECTUS_URL).with(rest());

export async function fetchPageBlocks(slug: string) {
  console.log(slug);
  const pages = await client.request(
    readItems("pages", {
      filter: {
        slug: { _eq: slug },
      },
      fields: [
        "*",
        {
          blocks: [
            "*",
            {
              item: {
                block_hero: ["*"],
                block_cardgroup: [
                  "*",
                  {
                    posts: [
                      "*.*", // Fetch all fields from related posts
                    ],
                    cards: [
                      "*.*", // Fetch all fields from related cards
                    ],
                  },
                ],
                block_richtext: ["*"],
              },
            },
          ],
        },
      ],
      limit: 1,
    })
  );
  return pages[0] || []; // Return blocks array or empty if not found
}
export default client;
```

