TITLE: Implementing User Registration API Route in Astro
DESCRIPTION: Creates an API route to handle user registration using Directus SDK, including automatic login after successful registration.
SOURCE: https://github.com/directus/docs/blob/main/content/tutorials/1.getting-started/using-authentication-in-astro.md#2025-04-09_snippet_4

LANGUAGE: ts
CODE:
```
import type { APIRoute } from "astro";
import client from "../../../lib/directus";
import { registerUser } from "@directus/sdk";

export const POST: APIRoute = async ({ request, cookies, redirect }) => {
  const formData = await request.formData();
  const firstName = formData.get('firstName') as string;
  const lastName = formData.get('lastName') as string;
  const email = formData.get("email") as string;
  const password = formData.get('password') as string;

  if (!firstName || !lastName ||!email || !password) {
    return new Response("All fields are required", { status: 400 });
  }

  try {
    const response = await client.request(registerUser(email, password, { first_name: firstName, last_name: lastName }));

    // Auto-login the user if registration is successful
    const loginResponse = await client.login(email, password);
    if (loginResponse.access_token) {
    cookies.set('directus_access_token', loginResponse.access_token);
  }
  } catch (error: any) {
    const message = error?.errors?.[0]?.message || 'Registration failed';
    return new Response(message, { status: 500 });
  }
  return redirect("/dashboard");
};
```

