TITLE: Implementing User Login API Route in Next.js
DESCRIPTION: Creates an API route for user login using Directus SDK's login function and handles form submission and cookie setting.
SOURCE: https://github.com/directus/docs/blob/main/content/tutorials/1.getting-started/using-authentication-in-next-js.md#2025-04-09_snippet_6

LANGUAGE: typescript
CODE:
```
import client from '@/lib/directus';
import { NextRequest, NextResponse } from 'next/server';
import { cookies } from 'next/headers';

export async function POST(request: NextRequest) {
    const formData = await request.formData();

    const email = formData.get("email") as string;
    const password = formData.get("password") as string;

    if (!email || !password) {
        return NextResponse.json({ error: "All fields are required" }, { status: 400 });
    }

    try {
        const response = await client.login(email, password);
        console.log(response);
        if (response.access_token) {
          (await cookies()).set('directus_session_token', response.access_token, { sameSite: 'strict', path: '/', secure: true })
        }
        const url = request.nextUrl.clone();
        url.pathname = "/dashboard"
        return NextResponse.redirect(url);
    } catch (error) {
      console.log(error);
        return NextResponse.json({ error: "Registration failed" }, { status: 500 });
    }
}
```

