      {navItems.map(item => (
        (item.public || token?.value) && (
          <a href={item.href}>{item.label}</a>
        )
      ))}
      {
        token?.value && (
       <a href="/api/auth/logout">Sign out</a>
        )
      }
    </nav>
    <slot />
  </body>
</html>
