// This function takes a IUser, but we want to see if it's a SocketGuildUser
public void DoSomething(IUser user)
{
    // We check if the boxed object is our type
    if (user is SocketGuildUser)
    {
        // We cast from the interface to the definition itself
        var guildUser = user as SocketGuildUser;

        // We can now access its properties
        var nickname = guildUser.Nickname;
        var roles = guildUser.Roles;
        var guild = guildUser.Guild;
    }
}
