// BAD EXAMPLE - don't do this, may throw InvalidCastException
// if (channel is ITextChannel)
// {
//     var textChannel = (ITextChannel)channel;
//     // textChannel is always an ITextChannel here
// }

// GOOD EXAMPLE #1 - pattern matching (C# 7.0+)
if (channel is ITextChannel textChannel)
{
    // textChannel is always an ITextChannel here
}

