// extend type Query {
//   character(id: ID!): CharacterNode
// }

// interface CharacterNode {
//   id: ID!
// }

// type Wizard implements CharacterNode {
//   id: ID!
//   screenName: String!
//   spells: [String!]!
// }

// type Fighter implements CharacterNode {
//   id: ID!
//   screenName: String!
//   powerLevel: Int!
// }

