TITLE: Creating Linked Select Components
DESCRIPTION: Demonstrates how to create dependent select components where the options in the second select are determined by the selection in the first select. Implements a continent-country selection relationship.
SOURCE: https://github.com/DouyinFE/semi-design/blob/main/content/input/select/index-en-US.md#2025-04-12_snippet_12

LANGUAGE: jsx
CODE:
```
import React from 'react';
import { Select } from '@douyinfe/semi-ui';

class Link extends React.Component {
    get continents() {
        return ['Asia', 'Europe'];
    }
    get maps() {
        return {
            Asia: ['China', 'Korea'],
            Europe: ['United Kingdom', 'France', 'Germany'],
        };
    }
    constructor() {
        super();
        this.state = {
            continents: this.continents,
            maps: this.maps,
            countrys: this.maps[this.continents[0]],
            country: this.maps[this.continents[0]][0],
        };
        this.continentsChange = this.continentsChange.bind(this);
        this.countryChange = this.countryChange.bind(this);
    }

    continentsChange(newContinents) {
        const { maps } = this.state;
        this.setState({ countrys: maps[newContinents], country: maps[newContinents][0] });
    }

    countryChange(country) {
        this.setState({ country });
    }

    render() {
        const { continents, countrys, country } = this.state;
        return (
            <React.Fragment>
                <Select
                    style={{ width: '150px', margin: '10px' }}
                    onChange={this.continentsChange}
                    defaultValue={continents[0]}
                >
                    {continents.map(pro => (
                        <Select.Option value={pro} key={pro}>
                            {pro}
                        </Select.Option>
                    ))}
                </Select>
                <Select style={{ width: '150px', margin: '10px' }} value={country} onChange={this.countryChange}>
                    {countrys.map(c => (
                        <Select.Option value={c} key={c}>
                            {c}
                        </Select.Option>
                    ))}
                </Select>
            </React.Fragment>
        );
    }
}
