                    <Button icon={<IconPlus />} theme="light" style={{ marginRight: 8 }}>
                        Select a document
                    </Button>
                </Upload>
                <Button icon={<IconUpload />} theme="light" onClick={this.manulUpload}>
                    Start upload
                </Button>
            </div>
        );
    }
}
