LANGUAGE: typescript
CODE:
```
interface CascaderProps {
    prefix?: ReactNode;
    preventScroll?: boolean;
    restTagsPopoverProps?: PopoverProps;
    searchPlaceholder?: string;
    searchPosition?: 'trigger' | 'custom';
    separator?: string;
    showClear?: boolean;
    showNext?: 'click' | 'hover';
    showRestTagsPopover?: boolean;
    size?: 'large' | 'small' | 'default';
    stopPropagation?: boolean;
    disableStrictly?: boolean;
    style?: CSSProperties;
    suffix?: ReactNode;
    topSlot?: ReactNode;
    treeData?: CascaderData[];
    treeNodeFilterProp?: string;
    triggerRender?: (props: TriggerRenderProps) => ReactNode;
    value?: string | number | CascaderData | (string | number | CascaderData)[][];
    validateStatus?: 'default' | 'error' | 'warning';
    virtualizeInSearch?: { height: number; width: number; itemSize: number };
    zIndex?: number;
    enableLeafClick?: boolean;
    onBlur?: (e: MouseEvent) => void;
    onChange?: (value: string | number | CascaderData | (string | number | CascaderData)[]) => void;
    onClear?: () => void;
    onChangeWithObject?: boolean;
    onDropdownVisibleChange?: (visible: boolean) => void;
    onExceed?: (checkedItem: Entity[]) => void;
    onFocus?: (e: MouseEvent) => void;
    onListScroll?: (e: React.Event, panel: { panelIndex: number; activeNode: CascaderData; }) => void;
