```
function bigIntN(n: number): fc.Arbitrary<bigint> {
  return fc.bigInt({ min: 1n << BigInt(n - 1), max: (1n << BigInt(n - 1)) - 1n });
}

function bigUintN(n: number): fc.Arbitrary<bigint> {
