String thingId = "org.eclipse.ditto:carbon-monoxide-alarm";

// initialize the ditto-client
DittoClient dittoClient = ... ;

dittoClient.live().message(Options.condition("eq(features/alarm/properties/confirmed,false)"))
        .from(thingId)
        .subject("co-alarm")
