@Test
public void testValidate() {
    Settings settings = Settings.builder()
        .put("cluster.routing.allocation.disk.threshold_enabled", "false")
        .put("cluster.routing.allocation.disk.watermark.low", "85%")
        .put("cluster.routing.allocation.disk.watermark.low.max_headroom", "200GB")
        .put("cluster.routing.allocation.disk.watermark.high", "87%")
        .put("cluster.routing.allocation.disk.watermark.high.max_headroom", "100GB")
        .put("cluster.routing.allocation.disk.watermark.flood_stage", "97%")
        .put("cluster.routing.allocation.disk.watermark.flood_stage.max_headroom", "5GB")
        .put("cluster.routing.allocation.disk.watermark.flood_stage.frozen", "95%")
        .put("cluster.routing.allocation.disk.watermark.flood_stage.frozen.max_headroom", "20GB")
        .build();
    new DiskThresholdSettings(settings);

    settings = Settings.builder()
        .put("cluster.routing.allocation.disk.watermark.low", "85%")
        .put("cluster.routing.allocation.disk.watermark.high", "87%")
        .put("cluster.routing.allocation.disk.watermark.flood_stage", "97%")
        .build();
    new DiskThresholdSettings(settings);

    settings = Settings.builder()
        .put("cluster.routing.allocation.disk.watermark.low", "85%")
        .put("cluster.routing.allocation.disk.watermark.high", "87%")
        .put("cluster.routing.allocation.disk.watermark.flood_stage", "97%")
