public class LicenseState implements ClusterStateListener {
    private static final Logger logger = LogManager.getLogger(LicenseState.class);

    /** A supplier for the current cluster state */
    private final Supplier<ClusterState> clusterStateSupplier;
    private final List<LicenseStateListener> listeners;
    /** Current active license **/
    private AtomicReference<License> currentLicense = new AtomicReference<>();

    private final AtomicBoolean nodesAllowedExecution = new AtomicBoolean(true);

    public LicenseState(Supplier<ClusterState> clusterStateSupplier) {
        this.clusterStateSupplier = clusterStateSupplier;
        this.listeners = new CopyOnWriteArrayList<>();
    }

    /**
     * Checks whether the current license if any is active
     * @return true if the license is active or no license is installed
     */
    public boolean isActive() {
        final License license = currentLicense.get();
        return license == null || license.status() == License.Status.ACTIVE;
    }

    // ... more methods ...
}
```

