TITLE: EuiSuperDatePicker with KQL Search Bar in React
DESCRIPTION: This example demonstrates the integration of EuiSuperDatePicker with Elastic's KQL search bar using EuiSearchBar. It includes state management for recently used ranges, loading indicators, refresh intervals, and time ranges.  It also handles outside clicks on the search bar to control the visibility of quick select options.
SOURCE: https://github.com/elastic/eui/blob/main/packages/website/docs/components/forms/date-and-time/super-date-picker.mdx#_snippet_7

LANGUAGE: tsx
CODE:
```
import React, { useState } from 'react';
import {
  EuiButtonEmpty,
  EuiButtonIcon,
  EuiFlexGroup,
  EuiFlexItem,
  EuiSearchBar,
  EuiSuperDatePicker,
  EuiSuperDatePickerProps,
  OnRefreshChangeProps,
  OnRefreshProps,
  OnTimeChangeProps,
  EuiOutsideClickDetector,
} from '@elastic/eui';

export default () => {
  const [recentlyUsedRanges, setRecentlyUsedRanges] = useState<
    NonNullable<EuiSuperDatePickerProps['recentlyUsedRanges']>
  >([]);
  const [isLoading, setIsLoading] = useState(false);
  const [refreshInterval, setRefreshInterval] = useState(1000);
  const [isPaused, setIsPaused] = useState(true);
  const [start, setStart] = useState('now-30m');
  const [end, setEnd] = useState('now');
  const [showQuickSelectOnly, setShowQuickSelectOnly] = useState(false);

  const onTimeChange = ({ start, end }: OnTimeChangeProps) => {
    const recentlyUsedRange = recentlyUsedRanges.filter((recentlyUsedRange) => {
      const isDuplicate =
        recentlyUsedRange.start === start && recentlyUsedRange.end === end;
      return !isDuplicate;
    });
    recentlyUsedRange.unshift({ start, end });
    setStart(start);
    setEnd(end);
    setRecentlyUsedRanges(
      recentlyUsedRange.length > 10
        ? recentlyUsedRange.slice(0, 9)
        : recentlyUsedRange
    );
    setIsLoading(true);
    startLoading();
  };

  const onRefresh = ({ start, end, refreshInterval }: OnRefreshProps) => {
    return new Promise((resolve) => {
      setTimeout(resolve, 100);
    }).then(() => {
      console.log(start, end, refreshInterval);
    });
  };

  const startLoading = () => {
    setTimeout(stopLoading, 1000);
  };

  const stopLoading = () => {
    setIsLoading(false);
  };

  const onRefreshChange = ({
    isPaused,
    refreshInterval,
  }: OnRefreshChangeProps) => {
    setIsPaused(isPaused);
    setRefreshInterval(refreshInterval);
  };

  return (
    <>
      <EuiFlexGroup gutterSize="s">
        <EuiFlexItem style={{ minWidth: '50%' }}>
          <EuiOutsideClickDetector
            onOutsideClick={() => setShowQuickSelectOnly(false)}
            isDisabled={!showQuickSelectOnly}
          >
            <EuiSearchBar
              box={{
                onClick: () => setShowQuickSelectOnly(true),
                onFocus: () => setShowQuickSelectOnly(true),
                onBlur: () => setShowQuickSelectOnly(false),
                prepend:
                  (<EuiButtonIcon aria-label="Saved searches" iconType="save" />),
                append: <EuiButtonEmpty>KQL</EuiButtonEmpty>,
                'aria-label': 'Filter using KQL',
                placeholder: 'type:visualization -dashboard',
              }}
            />
          </EuiOutsideClickDetector>
        </EuiFlexItem>
        <EuiFlexItem grow={false}>
          <div>
            <EuiSuperDatePicker
              start={start}
              end={end}
              onTimeChange={onTimeChange}
              onRefresh={onRefresh}
              isPaused={isPaused}
              isLoading={isLoading}
              refreshInterval={refreshInterval}
              onRefreshChange={onRefreshChange}
              recentlyUsedRanges={recentlyUsedRanges}
              width="auto"
              isQuickSelectOnly={showQuickSelectOnly}
            />
          </div>
        </EuiFlexItem>
      </EuiFlexGroup>
    </>
  );
};
```

