import React, { useState } from 'react';
import {
  EuiRange,
  EuiRangeProps,
  EuiSpacer,
  EuiDualRange,
  EuiDualRangeProps,
  useGeneratedHtmlId,
} from '@elastic/eui';

export default () => {
  const [value, setValue] = useState<EuiRangeProps['value']>('20');
