TITLE: Basic EuiSuperDatePicker Implementation
DESCRIPTION: This interactive example showcases the basic usage of the `EuiSuperDatePicker` component. It includes state management for start and end dates, loading state, and event handlers for time changes and refresh intervals. It also includes input fields to test resilience to invalid dates.
SOURCE: https://github.com/elastic/eui/blob/main/packages/website/docs/components/forms/date-and-time/super-date-picker.mdx#_snippet_1

LANGUAGE: typescript
CODE:
```
import React, { useState } from 'react';
import {
  EuiSuperDatePicker,
  EuiSpacer,
  EuiFormControlLayoutDelimited,
  EuiFormLabel,
  EuiFieldText,
  EuiPanel,
  EuiText,
  OnRefreshProps,
  OnTimeChangeProps,
} from '@elastic/eui';

export default () => {
  const [isLoading, setIsLoading] = useState(false);
  const [start, setStart] = useState('now-30m');
  const [end, setEnd] = useState('now');

  const onTimeChange = ({ start, end }: OnTimeChangeProps) => {
    setStart(start);
    setEnd(end);
    setIsLoading(true);
    startLoading();
  };

  const onRefresh = ({ start, end, refreshInterval }: OnRefreshProps) => {
    return new Promise((resolve) => {
      setTimeout(resolve, 100);
    }).then(() => {
      console.log(start, end, refreshInterval);
    });
  };

  const onStartInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setStart(e.target.value);
  };

  const onEndInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEnd(e.target.value);
  };

  const startLoading = () => {
    setTimeout(stopLoading, 1000);
  };

  const stopLoading = () => {
    setIsLoading(false);
  };

  const renderTimeRange = () => {
    return (
      <EuiPanel color="subdued" paddingSize="m">
        <EuiText size="s">
          EuiSuperDatePicker should be resilient to invalid date values. You can
          try to break it with unexpected values here.
        </EuiText>
        <EuiSpacer />
        <EuiFormControlLayoutDelimited
          prepend={<EuiFormLabel>Dates</EuiFormLabel>}
          startControl={
            <EuiFieldText
              controlOnly
              onChange={onStartInputChange}
              value={start}
              placeholder="start"
            />
          }
          endControl={
            <EuiFieldText
              controlOnly
              onChange={onEndInputChange}
              placeholder="end"
              value={end}
            />
          }
        />
      </EuiPanel>
    );
  };

  return (
    <>
      {renderTimeRange()}
      <EuiSpacer />
      <EuiSuperDatePicker
        isLoading={isLoading}
        start={start}
        end={end}
        onTimeChange={onTimeChange}
        onRefresh={onRefresh}
      />
    </>
  );
};
