export default () => {
  return (
    <>
      <EuiCode>{generateId()}</EuiCode>
      <br />
      <EuiCode>{generateId()}</EuiCode>
      <br />
      <EuiCode>{generateId()}</EuiCode>
      <br />
      <EuiCode>{generateId('hello')}</EuiCode>
