TITLE: Conveying Status with EuiSteps - React
DESCRIPTION: This example demonstrates how to use the `status` prop to convey the status of a step. It uses state to control the status and provides buttons to change the status between 'incomplete', 'complete', 'warning', 'danger', and 'disabled'. It shows how to integrate EuiButton and EuiFlexGroup to create interactive status changes for a step.
SOURCE: https://github.com/elastic/eui/blob/main/packages/website/docs/components/navigation/steps/index.mdx#_snippet_4

LANGUAGE: tsx
CODE:
```
import React, { useState } from 'react';
import {
  EuiSpacer,
  EuiSteps,
  EuiStepStatus,
  EuiButton,
  EuiFlexGroup,
  EuiFlexItem,
} from '@elastic/eui';

export default () => {
  const [status, setStatus] = useState<EuiStepStatus>('incomplete');

  let completeButton;
  if (status !== 'complete') {
    completeButton = (
      <EuiButton onClick={() => setStatus('complete')}>
        You complete me
      </EuiButton>
    );
  } else {
    completeButton = (
      <EuiButton onClick={() => setStatus('incomplete')}>Reset</EuiButton>
    );
  }

  let warningButton;
  if (status !== 'warning') {
    warningButton = (
      <EuiButton color="warning" onClick={() => setStatus('warning')}>
        Uh oh!
      </EuiButton>
    );
  } else {
    warningButton = (
      <EuiButton color="warning" onClick={() => setStatus('incomplete')}>
        Reset
      </EuiButton>
    );
  }

  let dangerButton;
  if (status !== 'danger') {
    dangerButton = (
      <EuiButton color="danger" onClick={() => setStatus('danger')}>
        Something terrible
      </EuiButton>
    );
  } else {
    dangerButton = (
      <EuiButton color="danger" onClick={() => setStatus('incomplete')}>
        Reset
      </EuiButton>
    );
  }

  let disabledButton;
  if (status !== 'disabled') {
    disabledButton = (
      <EuiButton color="accent" onClick={() => setStatus('disabled')}>
        Disabled
      </EuiButton>
    );
  } else {
    disabledButton = (
      <EuiButton color="accent" onClick={() => setStatus('incomplete')}>
        Reset
      </EuiButton>
    );
  }

  const firstSetOfSteps = [
    {
      title: 'Normal step',
      children: <p>Do this first</p>,
    },
    {
      title: 'Push the button to complete this final step',
      children: (
        <>
          <p>We are fancy buttons just waiting to be pushed!</p>
          <EuiSpacer />
          <EuiFlexGroup
            gutterSize="s"
            alignItems="center"
            responsive={false}
            wrap
          >
            <EuiFlexItem grow={false}> {completeButton} </EuiFlexItem>
            <EuiFlexItem grow={false}> {warningButton} </EuiFlexItem>
            <EuiFlexItem grow={false}> {dangerButton} </EuiFlexItem>
            <EuiFlexItem grow={false}> {disabledButton} </EuiFlexItem>
          </EuiFlexGroup>
        </>
      ),
      status: status,
    },
  ];

  return <EuiSteps steps={firstSetOfSteps} />;
};

