        />
      </EuiFormRow>

      <EuiSpacer size="m" />

      <EuiFormRow label="Korea">
        <EuiDatePicker
          selected={startDate}
          showTimeSelect
          onChange={handleChange}
          locale="ko"
          dateFormat="YYYY-MM-DD hh:mm A"
        />
      </EuiFormRow>

      <EuiSpacer size="m" />

