      </EuiFocusTrap>
      <EuiSpacer size="l" />
      <EuiText>
        The button below is not focusable by keyboard as long as the focus trap
        is enabled.
      </EuiText>
      <EuiButton onClick={() => {}}>External Focusable Element</EuiButton>
    </div>
  );
};
