```
export default () => {
    const chartBaseTheme = useChartBaseTheme();

    const customTicks = [
      {
        label: '',
        value: 0,
      },
      {
        label: '',
        value: 1,
      },
      {
        label: '',
        value: 2,
      },
      {
        label: '',
        value: 3,
      },
      {
        label: '',
        value: 4,
      },
    ];

    const customColorsLevels = [
      {
        min: 0,
        max: 1,
        color: '#a2cb9f',
      },
      {
        min: 1,
        max: 2,
        color: '#a1cbea',
      },
      {
        min: 2,
        max: 3,
        color: '#f2cc8f',
      },
      {
        min: 3,
        max: 4,
        color: '#e07a5f',
      },
      {
        min: 4,
        max: 5,
        color: '#b1130a',
      },
    ];
    const [customColorsValue, setCustomColorsValue] = useState(5 - 3.364726);

    const formatter = new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      notation: 'compact',
