        <EuiFlexGroup>
          <EuiFlexItem>
            <EuiPanel>
              <EuiStat
                title={resizeTrigger ?? ''}
                titleSize="m"
                description="Trigger"
                isLoading={!resizeTrigger}
              />
            </EuiPanel>
          </EuiFlexItem>
          <EuiFlexItem>
            <EuiPanel>
              <EuiStat
                title={`${Math.round(sizes[firstPanelId])}%`}
                titleSize="m"
                description="First panel"
                isLoading={!resizeTrigger}
              />
            </EuiPanel>
          </EuiFlexItem>
          <EuiFlexItem>
            <EuiPanel>
              <EuiStat
                title={`${Math.round(sizes[secondPanelId])}%`}
                titleSize="m"
                description="Second panel"
                isLoading={!resizeTrigger}
              />
            </EuiPanel>
          </EuiFlexItem>
        </EuiFlexGroup>
      </EuiFlexItem>
      <EuiFlexItem>
        <EuiResizableContainer
          style={{ height: '200px' }}
          onPanelWidthChange={(newSizes) => {
            setSizes((prevSizes) => ({ ...prevSizes, ...newSizes }));
          }}
          onResizeStart={(trigger) => setResizeTrigger(trigger)}
          onResizeEnd={() => setResizeTrigger(undefined)}
        >
          {(EuiResizablePanel, EuiResizableButton) => (
            <>
              <EuiResizablePanel
                id={firstPanelId}
                size={sizes[firstPanelId]}
                minSize="30%"
                tabIndex={0}
              >
                <EuiText>
                  <div>{text}</div>
                  <a href="">Hello world</a>
                </EuiText>
              </EuiResizablePanel>

              <EuiResizableButton accountForScrollbars="before" />

              <EuiResizablePanel
                id={secondPanelId}
                size={sizes[secondPanelId]}
                minSize="200px"
                tabIndex={0}
              >
                <EuiText>{text}</EuiText>
              </EuiResizablePanel>
            </>
          )}
        </EuiResizableContainer>
      </EuiFlexItem>
    </EuiFlexGroup>
  );
};
