```
import React, { useState } from 'react';
import {
  EuiCallOut,
  EuiFlyout,
  EuiFlyoutHeader,
  EuiFlyoutBody,
  EuiLink,
  EuiButton,
  EuiText,
  EuiTitle,
  useGeneratedHtmlId,
} from '@elastic/eui';

export default () => {
  const [isFlyoutVisible, setIsFlyoutVisible] = useState(false);
  const bannerFlyoutTitleId = useGeneratedHtmlId({
    prefix: 'bannerFlyoutTitle',
