TITLE: Using Color Tokens in a Specific Color Mode
DESCRIPTION: This example demonstrates how to use color tokens (e.g., euiTheme.colors.lightShade) within a specific color mode. It uses EuiThemeProvider to apply a selected color mode to a component, allowing you to dynamically change the appearance of elements based on the chosen theme.
SOURCE: https://github.com/elastic/eui/blob/main/packages/website/docs/getting-started/theming/color-mode.mdx#_snippet_1

LANGUAGE: tsx
CODE:
```
import React, { FunctionComponent, ReactNode, useState } from 'react';
import {
  EuiIcon,
  EuiSpacer,
  EuiText,
  EuiThemeProvider,
  useEuiTheme,
  EuiButtonGroup,
  EuiThemeColorMode,
} from '@elastic/eui';

const Box: FunctionComponent<{ children: ReactNode }> = ({ children }) => {
  const { euiTheme } = useEuiTheme();

  return (
    <EuiText
      css={{
        background: euiTheme.colors.lightShade,
        padding: euiTheme.size.xl,
      }}
    >
      <p>{children}</p>
    </EuiText>
  );
};

export default () => {
  const boxColorButtons = [
    {
      id: 'light',
      label: 'Light',
    },
    {
      id: 'dark',
      label: 'Dark',
    },
    {
      id: 'inverse',
      label: 'Inverse',
    },
  ];

  const [boxColorModeSelected, setBoxColorMode] =
    useState<EuiThemeColorMode>('light');

  const onChange = (colorMode: EuiThemeColorMode) => {
    setBoxColorMode(colorMode);
  };

  return (
    <>
      <EuiButtonGroup
        legend="Change color mode"
        options={boxColorButtons}
        idSelected={boxColorModeSelected}
        onChange={(id) => onChange(id as EuiThemeColorMode)}
      />

      <EuiSpacer />

      <EuiThemeProvider colorMode={boxColorModeSelected}>
        <Box>
          <EuiIcon type="faceHappy" /> The colors of this box is in{' '}
          <strong>{boxColorModeSelected}</strong> color mode
        </Box>
      </EuiThemeProvider>
    </>
  );
};

