TITLE: Creating Custom Pagination with EUI Components in React
DESCRIPTION: This snippet demonstrates creating a custom pagination layout using EuiFlexGroup, EuiContextMenu, and EuiPagination. It includes a dropdown for selecting the number of rows per page and an EuiPagination component for page navigation. It requires the `@elastic/eui` package and React.
SOURCE: https://github.com/elastic/eui/blob/main/packages/website/docs/components/navigation/pagination/index.mdx#_snippet_6

LANGUAGE: tsx
CODE:
```
import React, { useState } from 'react';
import {
  EuiButtonEmpty,
  EuiContextMenuItem,
  EuiContextMenuPanel,
  EuiFlexGroup,
  EuiFlexItem,
  EuiPagination,
  EuiPopover,
} from '@elastic/eui';

export default () => {
  const [isPopoverOpen, setIsPopoverOpen] = useState(false);
  const [activePage, setActivePage] = useState(0);
  const [rowSize, setRowSize] = useState(50);

  const PAGE_COUNT = 10;

  const onButtonClick = () =>
    setIsPopoverOpen((isPopoverOpen) => !isPopoverOpen);
  const closePopover = () => setIsPopoverOpen(false);

  const goToPage = (pageNumber: number) => setActivePage(pageNumber);

  const getIconType = (size: number) => {
    return size === rowSize ? 'check' : 'empty';
  };

  const button = (
    <EuiButtonEmpty
      size="xs"
      color="text"
      iconType="arrowDown"
      iconSide="right"
      onClick={onButtonClick}
    >
      Rows per page: {rowSize}
    </EuiButtonEmpty>
  );

  const items = [
    <
      key="10 rows"
      icon={getIconType(10)}
      onClick={() => {
        closePopover();
        setRowSize(10);
      }}
    >
      10 rows
    </EuiContextMenuItem>,
    <
      key="20 rows"
      icon={getIconType(20)}
      onClick={() => {
        closePopover();
        setRowSize(20);
      }}
    >
      20 rows
    </EuiContextMenuItem>,
    <
      key="50 rows"
      icon={getIconType(50)}
      onClick={() => {
        closePopover();
        setRowSize(50);
      }}
    >
      50 rows
    </EuiContextMenuItem>,
  ];

  return (
    <EuiFlexGroup
      justifyContent="spaceBetween"
      alignItems="center"
      responsive={false}
      wrap
    >
      <EuiFlexItem grow={false}>

          {button}
          <EuiContextMenuPanel items={items} />

      </EuiFlexItem>

      <EuiFlexItem grow={false}>

      </EuiFlexItem>
    </EuiFlexGroup>
  );
};
```

