      <EuiFormRow label="RGB(a) format" isInvalid={!!errors3} error={errors3}>
        <EuiColorPicker
          format="rgba"
          onChange={setColor3}
          color={color3}
          isInvalid={!!errors3}
          showAlpha={true}
        />
      </EuiFormRow>
    </>
  );
};
