  EuiFlyoutHeader,
  EuiPopover,
  EuiPopoverFooter,
  EuiPopoverTitle,
  EuiSelectable,
  EuiSelectableOption,
  EuiSelectableProps,
  EuiSpacer,
  EuiTitle,
  EuiInputPopover,
} from '@elastic/eui';

const OPTIONS: EuiSelectableOption[] = [
  { label: 'Titan' },
  { label: 'Enceladus' },
  { label: 'Mimas', checked: 'on' },
  { label: 'Dione' },
  { label: 'Iapetus' },
