  { label: 'Despina' },
  { label: 'Galatea' },
  { label: 'Larissa' },
  { label: 'Triton' },
  { label: 'Nereid' },
  { label: 'Charon' },
  { label: 'Styx' },
  { label: 'Nix' },
  { label: 'Kerberos' },
  { label: 'Hydra' },
];

const SelectablePopover = (
  props: Pick<EuiSelectableProps, 'options' | 'onChange'>
) => {
