import React, { useState } from 'react';

import {
  EuiCopy,
  EuiButton,
  EuiFieldText,
  EuiSpacer,
  EuiFormRow,
} from '@elastic/eui';

export default () => {
  const [copyText, setCopyText] = useState('I am the text that will be copied');

