TITLE: Collapsible Resizable Panels
DESCRIPTION: Illustrates the usage of collapsible panels within an EuiResizableContainer.  It demonstrates how to define panels as 'collapsible' or 'main' using the `mode` prop, which influences their behavior and the placement of the toggle button. It uses `EuiListGroup` and `EuiPanel` components to display content within the resizable panels.
SOURCE: https://github.com/elastic/eui/blob/main/packages/website/docs/components/containers/resizable-container.mdx#_snippet_5

LANGUAGE: tsx
CODE:
```
import React, { useState } from 'react';
import {
  EuiText,
  EuiResizableContainer,
  EuiListGroup,
  EuiListGroupItem,
  EuiPanel,
  EuiTitle,
  EuiSpacer,
  EuiPage,
} from '@elastic/eui';
import { faker } from '@faker-js/faker';

export default () => {
  const items = [
    {
      id: 1,
      label: 'First item',
      text: <p>{faker.lorem.paragraphs()}</p>,
      active: true,
    },
    {
      id: 2,
      label: 'Second item',
      text: <p>{faker.lorem.paragraphs()}</p>,
    },
    {
      id: 3,
      label: 'Third item',
      text: <p>{faker.lorem.paragraphs()}</p>,
    },
    {
      id: 4,
      label: 'Forth item',
      text: <p>{faker.lorem.paragraphs()}</p>,
    },
  ];

  const [itemSelected, setItemSelected] = useState(items[0]);
  const itemElements = items.map((item, index) => (
    <EuiListGroupItem
      key={index}
      onClick={() => setItemSelected(item)}
      label={item.label}
      size="s"
    />
  ));

  return (
    <>
      <EuiText>
        <h3>Simple</h3>
      </EuiText>
      <EuiSpacer />
      <EuiPage paddingSize="none">
        <EuiResizableContainer>
          {(EuiResizablePanel, EuiResizableButton) => (
            <>
              <EuiResizablePanel
                mode="collapsible"
                initialSize={20}
                minSize="10%"
              >
                <EuiListGroup flush>{itemElements}</EuiListGroup>
              </EuiResizablePanel>

              <EuiResizableButton />

              <EuiResizablePanel mode="main" initialSize={80} minSize="50px">
                <EuiPanel paddingSize="l" style={{ minHeight: '100%' }}>
                  <EuiTitle>
                    <p>{itemSelected.label}</p>
                  </EuiTitle>
                  <EuiSpacer />
                  <EuiText>{itemSelected.text}</EuiText>
                </EuiPanel>
              </EuiResizablePanel>
            </>
          )}
        </EuiResizableContainer>
      </EuiPage>

      <EuiSpacer />
      <EuiText>
        <h3>Multiple collapsible panels</h3>
      </EuiText>
      <EuiSpacer />

      <EuiPage paddingSize="none">
        <EuiResizableContainer>
          {(EuiResizablePanel, EuiResizableButton) => (
            <>
              <EuiResizablePanel
                mode="collapsible"
                initialSize={20}
                minSize="10%"
              >
                <EuiListGroup flush>{itemElements}</EuiListGroup>
              </EuiResizablePanel>

              <EuiResizableButton />

              <EuiResizablePanel
                mode="main"
                initialSize={60}
                minSize="50px"
                tabIndex={0}
              >
                <EuiPanel paddingSize="l" style={{ minHeight: '100%' }}>
                  <EuiTitle>
                    <p>{itemSelected.label}</p>
                  </EuiTitle>
                  <EuiSpacer />
                  <EuiText>{itemSelected.text}</EuiText>
                </EuiPanel>
              </EuiResizablePanel>

              <EuiResizableButton />

              <EuiResizablePanel
                mode="collapsible"
                initialSize={20}
                minSize="10%"
              >
                <EuiListGroup flush>{itemElements}</EuiListGroup>
              </EuiResizablePanel>
            </>
          )}
        </EuiResizableContainer>
      </EuiPage>

      <EuiSpacer />
      <EuiText>
        <h3>Vertical collapsible panels</h3>
      </EuiText>
      <EuiSpacer />

      <EuiPage paddingSize="none">
        <EuiResizableContainer direction="vertical" style={{ height: '400px' }}>
          {(EuiResizablePanel, EuiResizableButton) => (
            <>
              <EuiResizablePanel
                mode="collapsible"
                initialSize={20}
                minSize="10%"
              >
                <EuiListGroup flush>{itemElements}</EuiListGroup>
              </EuiResizablePanel>

              <EuiResizableButton accountForScrollbars="before" />

              <EuiResizablePanel mode="main" initialSize={80} minSize="50px">
                <EuiPanel paddingSize="l" style={{ height: '100%' }}>
                  <EuiTitle>
                    <p>{itemSelected.label}</p>
                  </EuiTitle>
                  <EuiSpacer />
                  <EuiText>{itemSelected.text}</EuiText>
                </EuiPanel>
              </EuiResizablePanel>
            </>
          )}
        </EuiResizableContainer>
      </EuiPage>
    </>
  );
};
