TITLE: Implementing Todo MVC Application with Electric SQL in React
DESCRIPTION: This code implements a complete Todo MVC application using Electric SQL for data synchronization. It includes database setup, schema migration, and a React component with functionality for adding, toggling, and removing todo items. The component uses Electric's hooks for real-time data access and manipulation.
SOURCE: https://github.com/electric-sql/electric/blob/main/website/demos/todo-app.md#2025-04-14_snippet_0

LANGUAGE: tsx
CODE:
```
import { useLiveQuery } from 'electric-sql/react'
import { ElectricDatabase, electrify } from 'electric-sql/wa-sqlite'
import { useEffect, useRef, useState } from 'react'
import { Electric, schema } from '../generated/client'
import styles from './index.module.css'

const DEBUG = false

// Our todo item shape
type Todo = {
  id: string
  text: string
  completed: number
}

export default function Index () {
  // Track when we've connected to electric
  const [electric, setElectric] = useState<Electric | null>(null)

  // When we've connected, save a reference to Electric
  useEffect(() => {
    let isMounted = true

    const init = async () => {
      // Connect to Electric and setup our database
      const config = {
        debug: DEBUG,
        url: import.meta.env.VITE_ELECTRIC_URL
      }
      const { tablesConfig } = schema
      const conn = await ElectricDatabase.init('todo.db')
      const db = await electrify(conn, schema)
      await db.todos.sync()

      // Create the table if it doesn't exist already
      await conn.execute(`
        CREATE TABLE IF NOT EXISTS todos (
          id TEXT PRIMARY KEY,
          text TEXT NOT NULL,
          completed INTEGER NOT NULL DEFAULT 0
        )
      `)

      if (!isMounted) return

      setElectric(db)
    }

    init()

    return () => {
      isMounted = false
    }
  }, [])

  if (electric === null) {
    return <div>Connecting to Electric...</div>
  }

  return <Todos db={electric} />
}

function Todos({ db }: { db: Electric }) {
  const $input = useRef<HTMLInputElement>(null)
  const [filter, setFilter] = useState<'all' | 'active' | 'completed'>('all')

  // Get all todos
  const { results: todos } = useLiveQuery(
    db.todos.liveMany({
      orderBy: {
        rowid: 'asc',
      },
    }),
  )

  const activeCount = todos?.filter((t) => !t.completed).length ?? 0
  const completedCount = (todos?.length ?? 0) - activeCount

  // Filter the todos based on the current filter
  const filteredTodos = todos?.filter((todo) => {
    if (filter === 'active') return !todo.completed
    if (filter === 'completed') return todo.completed
    return true
  })

  return (
    <div className={styles.app}>
      <header className={styles.header}>
        <h1>todos</h1>

        <form
          onSubmit={(e) => {
            e.preventDefault()
            const text = $input.current?.value.trim()
            if (!text) return

            // Add a new todo
            db.todos.create({
              data: {
                id: Date.now().toString(),
                text,
                completed: 0,
              },
            })

            // Clear the input
            if ($input.current) $input.current.value = ''
          }}
          className={styles.newTodo}
        >
          <input
            ref={$input}
            autoFocus
            className={styles.newTodoInput}
            placeholder="What needs to be done?"
          />
          <button type="submit" className={styles.submitButton}>
            Add
          </button>
        </form>
      </header>

      <section className={styles.main}>
        <ul className={styles.todoList}>
          {filteredTodos?.map((todo) => (
            <li key={todo.id} className={styles.todo}>
              <input
                type="checkbox"
                className={styles.toggle}
                checked={Boolean(todo.completed)}
                onChange={async () => {
                  // Toggle the todo's completed status
                  await db.todos.update({
                    where: {
                      id: todo.id,
                    },
                    data: {
                      completed: todo.completed ? 0 : 1,
                    },
                  })
                }}
              />
              <label className={styles.todoLabel}>{todo.text}</label>
              <button
                className={styles.destroy}
                onClick={async () => {
                  // Delete the todo
                  await db.todos.delete({
                    where: {
                      id: todo.id,
                    },
                  })
                }}
              />
            </li>
          ))}
        </ul>
      </section>

      {todos?.length ? (
        <footer className={styles.footer}>
          <span className={styles.todoCount}>
            {activeCount} {activeCount === 1 ? 'item' : 'items'} left
          </span>

          <ul className={styles.filters}>
            <li>
              <a
                className={filter === 'all' ? styles.selected : ''}
                onClick={() => setFilter('all')}
              >
                All
              </a>
            </li>
            <li>
              <a
                className={filter === 'active' ? styles.selected : ''}
                onClick={() => setFilter('active')}
              >
                Active
              </a>
            </li>
            <li>
              <a
                className={filter === 'completed' ? styles.selected : ''}
                onClick={() => setFilter('completed')}
              >
                Completed
              </a>
            </li>
          </ul>

          {completedCount > 0 && (
            <button
              className={styles.clearCompleted}
              onClick={async () => {
                // Delete all completed todos
                for (const todo of todos?.filter((t) => t.completed) ?? []) {
                  await db.todos.delete({
                    where: {
                      id: todo.id,
                    },
                  })
                }
              }}
            >
              Clear completed
            </button>
          )}
        </footer>
      ) : null}
    </div>
  )
}

```

