TITLE: Implementing Electric with Tanstack Query in React
DESCRIPTION: This code snippet demonstrates how to set up and use Electric with Tanstack Query in a React component. It includes functionality for fetching items, adding new items, and updating existing items with optimistic updates.
SOURCE: https://github.com/electric-sql/electric/blob/main/website/demos/tanstack.md#2025-04-14_snippet_0

LANGUAGE: tsx
CODE:
```
import { useEffect, useState } from 'react'
import { ElectricDatabase, electrify } from 'electric-sql/react'
import { authToken, schema } from './generated/client'

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'

export function Example() {
  const [db, setDb] = useState<ElectricDatabase>()

  useEffect(() => {
    const init = async () => {
      const config = await authToken()
      const { makeElectricContext, db } = await electrify(config)
      setDb(db)
    }

    init()
  }, [])

  if (db === undefined) {
    return <div>Loading...</div>
  }

  return <ExampleComponent db={db} />
}

function ExampleComponent({ db }: { db: ElectricDatabase }) {
  const { items } = db

  const queryClient = useQueryClient()

  const { data } = useQuery({
    queryKey: ['items'],
    queryFn: async () => {
      const results = await items.sync().then(() => items.findMany())
      return results
    },
  })

  const addMutation = useMutation({
    mutationFn: async (value: string) => {
      const item = await items.create({
        data: { value },
      })
      return item
    },
    onMutate: async (newValue) => {
      await queryClient.cancelQueries({ queryKey: ['items'] })
      const previousItems = queryClient.getQueryData(['items'])
      queryClient.setQueryData(['items'], (old: any) => [
        ...(old ?? []),
        { id: 'temp-id', value: newValue },
      ])
      return { previousItems }
    },
    onError: (err, newValue, context: any) => {
      queryClient.setQueryData(['items'], context.previousItems)
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: ['items'] })
    },
  })

  const updateMutation = useMutation({
    mutationFn: async ({ id, value }: { id: string; value: string }) => {
      const item = await items.update({
        where: { id },
        data: { value },
      })
      return item
    },
    onMutate: async (newItem) => {
      await queryClient.cancelQueries({ queryKey: ['items'] })
      const previousItems = queryClient.getQueryData(['items'])
      queryClient.setQueryData(['items'], (old: any) =>
        old.map((item: any) =>
          item.id === newItem.id ? { ...item, ...newItem } : item
        )
      )
      return { previousItems }
    },
    onError: (err, newItem, context: any) => {
      queryClient.setQueryData(['items'], context.previousItems)
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: ['items'] })
    },
  })

  const [newValue, setNewValue] = useState('')

  return (
    <div>
      <h1>Items</h1>
      <ul>
        {data?.map((item) => (
          <li key={item.id}>
            <input
              value={item.value}
              onChange={(e) =>
                updateMutation.mutate({ id: item.id, value: e.target.value })
              }
            />
          </li>
        ))}
      </ul>
      <input
        value={newValue}
        onChange={(e) => setNewValue(e.target.value)}
        onKeyDown={(e) => {
          if (e.key === 'Enter') {
            addMutation.mutate(newValue)
            setNewValue('')
          }
        }}
      />
    </div>
  )
}
```

