TITLE: Implementing ElectricSQL in a React Component
DESCRIPTION: A complete React component that demonstrates how to initialize ElectricSQL, connect to a database, and perform CRUD operations. It includes state management for items, form handling for adding new items, and real-time data updates.
SOURCE: https://github.com/electric-sql/electric/blob/main/website/demos/react.md#2025-04-14_snippet_0

LANGUAGE: tsx
CODE:
```
import { useLiveQuery } from 'electric-sql/react'
import { useEffect, useState } from 'react'
import { ElectricDatabase, Item, electrify } from './generated/client'

export function Example() {
  // State for the electric client
  const [electric, setElectric] = useState<ElectricDatabase>()

  // State for the form input
  const [value, setValue] = useState('')

  // Initialise the electric client
  useEffect(() => {
    const init = async () => {
      const config = {
        debug: true,
        url: import.meta.env.VITE_ELECTRIC_URL
      }

      const { db } = await electrify(config)
      setElectric(db)
    }

    init()
  }, [])

  // Use live query to reactively render items in the UI
  const { results } = useLiveQuery(
    electric?.db.item.liveMany({
      orderBy: { id: 'asc' }
    })
  )

  const items = results ?? []

  // Submit handler to insert a new item
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!electric || value === '') {
      return
    }

    await electric.db.item.create({
      data: {
        value,
        created_at: new Date().toISOString()
      }
    })

    setValue('')
  }

  // Toggle handler to update the is_complete field
  const toggle = async (item: Item) => {
    if (!electric) {
      return
    }

    await electric.db.item.update({
      where: { id: item.id },
      data: { is_complete: !item.is_complete }
    })
  }

  // Delete handler to delete an item
  const doDelete = async (item: Item) => {
    if (!electric) {
      return
    }

    await electric.db.item.delete({
      where: { id: item.id }
    })
  }

  return (
    <div>
      <h3>Items</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="add"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          required
        />
        <button type="submit">Add</button>
      </form>
      <ul>
        {items.map((item) => (
          <li
            key={item.id}
            className={item.is_complete ? 'complete' : undefined}
          >
            <span onClick={() => toggle(item)}>{item.value}</span>
            <button onClick={() => doDelete(item)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  )
}

```

