TITLE: Implementing a Custom Yjs Provider with Electric SQL in TypeScript
DESCRIPTION: This code implements a Yjs Provider that syncs Yjs operations with Electric SQL. It establishes a bidirectional connection between Yjs documents and the Electric SQL backend, handling document updates, awareness states, and synchronization between clients.
SOURCE: https://github.com/electric-sql/electric/blob/main/website/demos/yjs.md#2025-04-14_snippet_0

LANGUAGE: typescript
CODE:
```
import * as Y from "yjs"
import { DocProviderConfig, ElectricClient } from "electric-sql/client"
import { GenericEventCallback } from "electric-sql/util"
import { LiveSelect } from "electric-sql/client/model"
import { DatabaseAdapter } from "electric-sql/drivers/websql/adapters"
import { Subscription } from "electric-sql/rx"

// An Electric provider for Yjs
//
// This is an example implementation of a Yjs Provider using Electric as the
// backend. It's not currently fully feature complete but demonstrates the basic
// principles.
export class ElectricProvider {
  private name: string
  private doc: Y.Doc
  private db: any
  private dbAdapter: DatabaseAdapter
  private client: ElectricClient
  private docProviderConfig: DocProviderConfig | undefined
  private _synced: boolean
  private _liveSelect: LiveSelect | undefined
  private _subscription: Subscription | undefined
  private _resyncRequired: boolean // indicates that a full doc update is required on next sync
  private _destroyed: boolean
  private _fetchingUpdates: boolean
  private _insertingUpdates: boolean
  private _observers: Map<string, GenericEventCallback<any>>

  /**
   * @param {string} name - A globally unique name for the document
   * @param {Y.Doc} doc - The Yjs document
   * @param {ElectricClient} client - An Electric client instance
   * @param {object} [opts] - Configuration options for the provider
   * @param {DocProviderConfig} [opts.docProviderConfig] - Configuration for the DocProvider
   */
  constructor(
    name: string,
    doc: Y.Doc,
    client: ElectricClient,
    opts: { docProviderConfig?: DocProviderConfig } = {}
  ) {
    this.name = name
    this.doc = doc
    this.client = client
    this.db = client.db
    this.dbAdapter = client.adapter
    this.docProviderConfig = opts.docProviderConfig

    this._synced = false
    this._resyncRequired = true
    this._destroyed = false
    this._fetchingUpdates = false
    this._insertingUpdates = false
    this._observers = new Map()
  }

  /**
   * A promise that resolves once the provider is synced.
   *
   * @return {Promise<any>}
   */
  get synced() {
    if (this._synced) {
      return Promise.resolve()
    }

    return new Promise<void>((resolve) => {
      this._observers.set("synced", () => {
        this._observers.delete("synced")
        resolve()
      })
    })
  }

  /**
   * Connect to the Electric backend and sync with the document.
   */
  connect() {
    this.setupDbHooks()
    this.fetchUpdates().then(() => this.setupDocHooks())
  }

  /**
   * Disconnect from the Electric backend.
   */
  disconnect() {
    this._subscription?.unsubscribe()
    this._observers.clear()
    this._destroyed = true
  }

  /**
   * Destroy the provider and clean up resources.
   */
  destroy() {
    this.disconnect()
  }

  /**
   * Emit an event.
   *
   * @param {string} name - The name of the event
   * @param {any} args - Arguments to pass to the event handlers
   * @private
   */
  emit(name: string, args: any) {
    const callback = this._observers.get(name)
    if (callback) callback(args)
  }

  /**
   * Set up the hooks to listen for changes in the database.
   * @private
   */
  setupDbHooks() {
    // Set up a live select query for the document
    this._liveSelect = this.client.liveSelect("updates")
      .where("docName", "=", this.name)
      .orderBy("timestamp", "asc")

    // Subscribe to changes in the updates table
    this._subscription = this._liveSelect.subscribe((updates) => {
      // Skip if we're already fetching or destroyed
      if (this._fetchingUpdates || this._destroyed) return

      this.fetchUpdates()
    })
  }

  /**
   * Set up hooks to listen for changes in the Yjs document.
   * @private
   */
  setupDocHooks() {
    // Listen for changes to the document
    this.doc.on("update", (update: Uint8Array, origin: any) => {
      // Skip if the update came from us
      if (origin === this) return

      // Apply the update to the database
      this.insertUpdate(update)
    })
  }

  /**
   * Fetch updates from the database and apply them to the document.
   * @private
   */
  async fetchUpdates() {
    // Skip if we're already fetching or destroyed
    if (this._fetchingUpdates || this._destroyed) return

    try {
      this._fetchingUpdates = true

      // If resyncing is required, we apply the document as a whole
      if (this._resyncRequired) {
        // Get the document state if it exists
        const docRow = await this.dbAdapter.query({
          sql: "SELECT * FROM documents WHERE name = ?",
          args: [this.name]
        })

        // If we have a document state, apply it to the doc
        if (docRow.rows.length > 0) {
          const docData = docRow.rows.item(0)
          const docContent = new Uint8Array(JSON.parse(docData.content))
          Y.applyUpdate(this.doc, docContent, this)
        }

        this._resyncRequired = false
      }

      // Fetch updates from the database
      const updatesResult = await this.dbAdapter.query({
        sql: "SELECT * FROM updates WHERE docName = ? ORDER BY timestamp ASC",
        args: [this.name]
      })

      // Apply each update to the document
      for (let i = 0; i < updatesResult.rows.length; i++) {
        const update = updatesResult.rows.item(i)
        const updateContent = new Uint8Array(JSON.parse(update.content))

        Y.applyUpdate(this.doc, updateContent, this)
      }

      // Set synced state and emit event
      const wasSynced = this._synced
      this._synced = true

      if (!wasSynced) {
        this.emit("synced", null)
      }
    } catch (e) {
      console.error("Error fetching updates:", e)
    } finally {
      this._fetchingUpdates = false
    }
  }

  /**
   * Insert an update into the database.
   *
   * @param {Uint8Array} update - The update to insert
   * @private
   */
  async insertUpdate(update: Uint8Array) {
    // Skip if we're already inserting or destroyed
    if (this._insertingUpdates || this._destroyed) return

    try {
      this._insertingUpdates = true

      // Store the current document state
      const docContent = Y.encodeStateAsUpdate(this.doc)
      await this.dbAdapter.run({
        sql: `INSERT INTO documents (name, content) 
              VALUES (?, ?) 
              ON CONFLICT(name) DO UPDATE SET content = excluded.content`,
        args: [this.name, JSON.stringify(Array.from(docContent))]
      })

      // Add the update to the updates table
      await this.dbAdapter.run({
        sql: "INSERT INTO updates (docName, content, timestamp) VALUES (?, ?, ?)",
        args: [
          this.name,
          JSON.stringify(Array.from(update)),
          new Date().getTime()
        ]
      })
    } catch (e) {
      console.error("Error inserting update:", e)
    } finally {
      this._insertingUpdates = false
    }
  }
}
```

