TITLE: Syncing Electric SQL Data into Redis Cache
DESCRIPTION: Demonstrates how to set up data synchronization from Electric SQL into Redis. The code establishes a Redis client, subscribes to Electric changes using DAL (Data Access Layer), and updates Redis hash structures when data changes occur. It handles both inserts/updates and deletes automatically.
SOURCE: https://github.com/electric-sql/electric/blob/main/website/demos/redis.md#2025-04-14_snippet_0

LANGUAGE: typescript
CODE:
```
import { RedisClientType, createClient } from 'redis';
import { makeElectricClient } from 'electric-sql/node';
import { genUUID } from 'electric-sql/util';
import { config } from 'dotenv';
import { isNotNull } from 'electric-sql/util';
import type { Database } from './generated/client';

config();

const migrationsTableName = '_migrations';
const updatedAtFieldName = 'updated_at';
const hashKeyPrefix = 'item';

const redisUrl = process.env.REDIS_URL || 'redis://localhost:6379';

const main = async (): Promise<void> => {
  // Setup Redis
  const redisClient: RedisClientType = createClient({
    url: redisUrl,
  });
  await redisClient.connect();

  // Setup Electric
  const conn = process.env.DATABASE_URL || 'postgresql://postgres:password@localhost:5432/electric';

  const client = await makeElectricClient({
    config: {
      app: genUUID(),
      migrations: {
        migrationsTable: migrationsTableName,
        migrationsDirs: [
          { path: './migrations', namespace: 'default' },
        ],
      },
      connection: {
        type: 'postgresql',
        host: conn.split('://')?.[1]?.split(':')?.[1]?.split('@')?.[1] || 'localhost',
        port: parseInt(conn.split('://')?.[1]?.split(':')?.[2]?.split('/')?.[0] || '5432'),
        database: conn.split('/')?.[3] || 'electric',
        user: conn.split('://')?.[1]?.split(':')?.[1]?.split('@')?.[0] || 'postgres',
        password: conn.split('://')?.[1]?.split(':')?.[2]?.split('@')?.[0] || 'password',
      },
      debug: true,
    },
  }) as Promise<Database>;

  // Subscribe to changes
  const notifier = client.notifier;
  notifier.subscribeToShapes({
    items: {
      select: {
        id: true,
        title: true,
        description: true,
        created_at: true,
        updated_at: true,
      },
    },
  });

  const dal = client._electric!;
  dal.satellite.notifier.potentiallyChanged$.subscribe((shapes) => {
    console.log('Shapes changed', shapes);

    if (shapes.includes('items')) {
      client.items.sync
        .findMany({
          where: {
            // Only sync items that have been updated after the last sync
            // Use updatedAtFieldName as we're polling for changes
            [updatedAtFieldName]: { gte: new Date(Date.now() - 30 * 1000) },
          },
        })
        .then(async (items) => {
          console.log('Syncing items', items);

          // Get the current keys in Redis that match our prefix
          try {
            const keys = await redisClient.keys(`${hashKeyPrefix}:*`);

            // Process each key and get the ID (suffix)
            const suffixes = new Set(
              keys.map((key) => key.split(':')[1]).filter(isNotNull)
            );

            // Add or update items in Redis
            for (const item of items) {
              if (!item?.[updatedAtFieldName]) {
                console.log(`Item ${item.id} has no ${updatedAtFieldName}`);
                continue;
              }

              await redisClient.hSet(
                `${hashKeyPrefix}:${item.id}`,
                'id',
                item.id,
                'title',
                item.title,
                'description',
                item.description ? item.description : '',
                'created_at',
                item.created_at ? item.created_at.toISOString() : '',
                'updated_at',
                item?.[updatedAtFieldName].toISOString()
              );

              // Remove from the set of suffixes because it exists
              suffixes.delete(item.id);
            }

            // Remove any items in Redis that no longer exist (weren't returned in our query)
            for (const suffix of suffixes) {
              await redisClient.del(`${hashKeyPrefix}:${suffix}`);
            }

            console.log(`Synced ${items.length} items`);
          } catch (e) {
            console.error('Error syncing items', e);
          }
        });
    }
  });

  // Keep the process running
  process.stdin.resume();
};

main();
```

