TITLE: Implementing Electric SQL in Next.js Page Component
DESCRIPTION: Main page component implementation showing Electric SQL integration with Next.js. Demonstrates client-side data synchronization setup and basic UI rendering with TypeScript and React.
SOURCE: https://github.com/electric-sql/electric/blob/main/website/demos/nextjs.md#2025-04-14_snippet_0

LANGUAGE: tsx
CODE:
```
"use client";

import { useEffect, useState } from 'react';
import { ElectricDatabase, electrify } from 'electric-sql/wa-sqlite';
import { genUUID } from 'electric-sql/util';
import { Electric, Items, schema } from './.electric/client';
import { ItemShape } from './shapes';

export default function Home() {
  const [electric, setElectric] = useState<Electric>();
  const [items, setItems] = useState<Items[]>([]);
  const [input, setInput] = useState<string>('');

  useEffect(() => {
    let isMounted = true;

    const init = async () => {
      const config = {
        app: 'my-app',
        env: 'local',
        token: process.env.NEXT_PUBLIC_ELECTRIC_TOKEN,
      };
      const conn = await electrify(config);
      const electric = await conn.db(schema);

      if (!isMounted) {
        return;
      }

      setElectric(electric);

      const { results } = await electric.db.raw({
        sql: `SELECT name FROM items ORDER BY position ASC`,
      });

      setItems(results);

      const subscription = await electric.items.sync();

      const { results: liveResults } = await electric.db.raw({
        sql: `SELECT name FROM items ORDER BY position ASC`,
      });

      subscription.on('update', ({ results: updatedResults }) => {
        setItems(updatedResults);
      });

      setItems(liveResults);
    };

    init();

    return () => {
      isMounted = false;
    };
  }, []);

  const addItem = async () => {
    if (!electric || !input) return;

    const item: ItemShape = {
      id: genUUID(),
      name: input,
      position: items.length,
    };

    await electric.items.create(item);

    setInput('');
  };

  return (
    <main className="flex min-h-screen flex-col items-center justify-between p-24">
      <div className="z-10 w-full max-w-5xl items-center justify-between font-mono text-sm">
        <h1 className="text-4xl font-bold mb-8">Items</h1>

        <div className="mb-8 flex gap-4">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyUp={(e) => e.key === 'Enter' && addItem()}
            className="flex-1 rounded border p-2 text-gray-900"
            placeholder="Add an item"
          />

          <button
            onClick={addItem}
            className="rounded bg-blue-500 px-4 py-2 font-bold text-white hover:bg-blue-700"
          >
            Add
          </button>
        </div>

        <ul className="space-y-4">
          {items.map((item: Items, index: number) => (
            <li key={index} className="rounded bg-gray-800 p-4">
              {item.name}
            </li>
          ))}
        </ul>
      </div>
    </main>
  );
}

```

