export default DS.RESTSerializer.extend({
  normalize: function(type, hash, prop) {
    hash = this._super(type, hash, prop);
    if (!hash.hasOwnProperty('firstName')) {
      hash.firstName = null;
    }
    if (!hash.hasOwnProperty('lastName')) {
      hash.lastName = null;
    }
    return hash;
  },
});
