TITLE: Default API Authentication Helpers in RedwoodJS (TypeScript)
DESCRIPTION: This TypeScript code shows the default implementation of authentication helper functions in `api/src/lib/auth.ts`, with type annotations. It includes `getCurrentUser`, which retrieves the user based on the session ID (initially selecting only the `id`), `isAuthenticated` to check login status, `hasRole` for role-based access control, and `requireAuth` to enforce authentication and roles, throwing errors if checks fail. Dependencies include `@redwoodjs/graphql-server` for error types, `./db` for the Prisma client, and `@redwoodjs/api` for types like `DbAuthSession`.
SOURCE: https://github.com/emelleby/docwood/blob/main/docs/content/02_tutorial/07_chapter4/authentication.md#_snippet_14

LANGUAGE: typescript
CODE:
```
```ts title="api/src/lib/auth.ts"
import { AuthenticationError, ForbiddenError } from '@redwoodjs/graphql-server'
import { db } from './db'

import type { DbAuthSession } from '@redwoodjs/api'

export const getCurrentUser = async (session: DbAuthSession<number>) => {
  return await db.user.findUnique({
    where: { id: session.id },
    select: { id: true },
  })
}

export const isAuthenticated = (): boolean => {
  return !!context.currentUser
}

type AllowedRoles = string | string[] | undefined

export const hasRole = (roles: AllowedRoles): boolean => {
  if (!isAuthenticated()) {
    return false
  }

  const currentUserRoles = context.currentUser?.roles

  if (typeof roles === 'string') {
    if (typeof currentUserRoles === 'string') {
      // roles to check is a string, currentUser.roles is a string
      return currentUserRoles === roles
    } else if (Array.isArray(currentUserRoles)) {
      // roles to check is a string, currentUser.roles is an array
      return currentUserRoles?.some((allowedRole) => roles === allowedRole)
    }
  }

  if (Array.isArray(roles)) {
    if (Array.isArray(currentUserRoles)) {
      // roles to check is an array, currentUser.roles is an array
      return currentUserRoles?.some((allowedRole) =>
        roles.includes(allowedRole)
      )
    } else if (typeof currentUserRoles === 'string') {
      // roles to check is an array, currentUser.roles is a string
      return roles.some((allowedRole) => currentUserRoles === allowedRole)
    }
  }

  // roles not found
  return false
}

export const requireAuth = ({ roles }: { roles?: AllowedRoles } = {}) => {
  if (!isAuthenticated()) {
    throw new AuthenticationError("You don't have permission to do that.")
  }

  if (roles && !hasRole(roles)) {
    throw new ForbiddenError("You don't have access to do that.")
  }
}
