name: Redwood CD for database deployment\n\non:\n  push:\n    branches: ['main']\n\nenv:\n  DATABASE_URL: postgres://postgres:postgres@localhost:5432/postgres\n  TEST_DATABASE_URL: postgres://postgres:postgres@localhost:5432/postgres\n\njobs:\n  build:\n    runs-on: ubuntu-latest\n\n    strategy:\n      matrix:\n        node-version: [20.x]\n        # See supported Node.js release schedule at https://nodejs.org/en/about/releases/\n\n    services:\n      # Label used to access the service container\n      postgres:\n        # Docker Hub image\n        image: postgres\n        # Provide the password for postgres\n        env:\n          POSTGRES_PASSWORD: postgres\n        # Set health checks to wait until postgres has started\n        options: >-\n          --health-cmd pg_isready\n          --health-interval 10s\n          --health-timeout 5s\n          --health-retries 5\n        ports:\n          # Maps tcp port 5432 on service container to the host\n          - 5432:5432\n\n    steps:\n      - uses: actions/checkout@v4\n      - name: Use Node.js ${{ matrix.node-version }}\n        uses: actions/setup-node@v4\n        with:\n          node-version: ${{ matrix.node-version }}\n      - run: corepack enable\n      # install all the dependencies\n      - run: yarn install\n      # build the redwood app\n      - run: yarn rw build\n      # run the api tests\n      - run: yarn rw test api --no-watch\n      # run the web tests\n      - run: yarn rw test web --no-watch\n      # run migrations on the actual database\n      - run: yarn rw prisma migrate deploy\n      # run seed script in the actual db\n      - run: yarn rw prisma db seed\n
