CODE:
```
func Traverse(ctx context.Context, client *ent.Client) error {
	owner, err := client.Group.
		Query().
		Where(group.Name("Github")).
		QueryAdmin().
		QueryFriends().
		QueryPets().
		QueryFriends().
		QueryOwner().
		Only(ctx)
	if err != nil {
		return fmt.Errorf("failed querying the owner: %w", err)
	}
	fmt.Println(owner)
