	if err := client.Schema.Create(context.Background()); err != nil {
		log.Fatalf("failed creating schema resources: %v", err)
	}

	svc := entpb.NewUserService(client)

	server := grpc.NewServer()

	entpb.RegisterUserServiceServer(server, svc)

