func (User) Hooks() []ent.Hook {
	return []ent.Hook{
		hook.On(
			func(next ent.Mutator) ent.Mutator {
				return hook.UserFunc(func(ctx context.Context, m *gen.UserMutation) (ent.Value, error) {
					meta, ok := m.MetaJSON()
					if !ok {
						return next.Mutate(ctx, m)
					}
					if b, err := json.Marshal(meta); err != nil {
						return nil, err
					}
					m.SetMeta(b)
					return next.Mutate(ctx, m)
				})
			},
			ent.OpCreate,
		),
	}
}
