type DomainString string

func (d DomainString) Value() (driver.Value, error) {
    return string(d), nil
}

func (d *DomainString) Scan(src any) error {
    switch s := src.(type) {
    case string:
        *d = DomainString(s)
    case []byte:
        *d = DomainString(s)
    default:
        return fmt.Errorf("unexpected type for DomainString: %T", src)
    }
    return nil
}
