TITLE: Complete Integration Implementation
DESCRIPTION: Complete code example combining all components into a single file.
SOURCE: https://github.com/eslint/eslint/blob/main/docs/src/integrate/integration-tutorial.md#2025-04-14_snippet_6

LANGUAGE: javascript
CODE:
```
const { ESLint } = require("eslint");

function createESLintInstance(overrideConfig) {
	return new ESLint({
		overrideConfigFile: true,
		overrideConfig,
		fix: true,
	});
}

async function lintAndFix(eslint, filePaths) {
	const results = await eslint.lintFiles(filePaths);

	await ESLint.outputFixes(results);

	return results;
}

function outputLintingResults(results) {
	const problems = results.reduce(
		(acc, result) => acc + result.errorCount + result.warningCount,
		0,
	);

	if (problems > 0) {
		console.log("Linting errors found!");
		console.log(results);
	} else {
		console.log("No linting errors found.");
	}
	return results;
}

async function lintFiles(filePaths) {
	const overrideConfig = {
		languageOptions: {
			ecmaVersion: 2018,
			sourceType: "commonjs",
		},
		rules: {
			"no-console": "error",
			"no-unused-vars": "warn",
		},
	};

	const eslint = createESLintInstance(overrideConfig);
	const results = await lintAndFix(eslint, filePaths);
	return outputLintingResults(results);
}

module.exports = { lintFiles };
