TITLE: Implementing AmIRichAlready Smart Contract in Solidity
DESCRIPTION: A Solidity smart contract that checks if a user has enough tokens to be considered 'rich', using an ERC20 token interface dependency.

LANGUAGE: solidity
CODE:
```
pragma solidity ^0.6.2;

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
}

contract AmIRichAlready {
    IERC20 private tokenContract;
    uint public richness = 1000000 * 10 ** 18;

    constructor (IERC20 _tokenContract) public {
        tokenContract = _tokenContract;
    }

    function check() public view returns (bool) {
        uint balance = tokenContract.balanceOf(msg.sender);
        return balance > richness;
    }
}
