  title: "Atoms / Form / Button",
  component: Button
} satisfies Meta<ButtonType>

export default meta
type Story = StoryObj<typeof meta>;

export const Solid: Story = {
  args: {
    variant: 'solid',
    children: 'A Button'
  }
}
export const Outline: Story = {
  args: {
    variant: 'outline',
    children: 'A Button'
  }
}

