TITLE: Implementing a Simple Betting Game Smart Contract in Solidity
DESCRIPTION: A basic Solidity smart contract that implements a betting game where players can place bets and either win or lose. The contract tracks total games won and lost, and emits events when bets are placed.

LANGUAGE: solidity
CODE:
```
pragma solidity 0.7.1;

contract Game {
    uint256 totalGamesPlayerWon = 0;
    uint256 totalGamesPlayerLost = 0;
    event BetPlaced(address player, uint256 value, bool hasWon);

    function placeBet() external payable {
        bool hasWon = evaluateBetForPlayer(msg.sender);

        if (hasWon) {
            (bool success, ) = msg.sender.call{ value: msg.value * 2 }('');
            require(success, "Transfer failed");
            totalGamesPlayerWon++;
        } else {
            totalGamesPlayerLost++;
        }

        emit BetPlaced(msg.sender, msg.value, hasWon);
    }
}
