TITLE: Sending Requests with Schema Validation and Progress Support
DESCRIPTION: Sends a JSON-RPC request and waits for a response, with support for progress reporting, timeout management, and response validation using Zod schemas.
SOURCE: https://github.com/eyaltoledano/claude-task-master/blob/main/context/mcp-js-sdk-docs.txt#2025-04-23_snippet_175

LANGUAGE: typescript
CODE:
```
/**
 * Sends a request and wait for a response.
 *
 * Do not use this method to emit notifications! Use notification() instead.
 */
request<T extends ZodType<object>>(
  request: SendRequestT,
  resultSchema: T,
  options?: RequestOptions,
): Promise<z.infer<T>> {
  return new Promise((resolve, reject) => {
    if (!this._transport) {
      reject(new Error("Not connected"));
      return;
    }

    if (this._options?.enforceStrictCapabilities === true) {
      this.assertCapabilityForMethod(request.method);
    }

    options?.signal?.throwIfAborted();

    const messageId = this._requestMessageId++;
    const jsonrpcRequest: JSONRPCRequest = {
      ...request,
      jsonrpc: "2.0",
      id: messageId,
    };

    if (options?.onprogress) {
      this._progressHandlers.set(messageId, options.onprogress);
      jsonrpcRequest.params = {
        ...request.params,
        _meta: { progressToken: messageId },
      };
    }

    const cancel = (reason: unknown) => {
      this._responseHandlers.delete(messageId);
      this._progressHandlers.delete(messageId);
      this._cleanupTimeout(messageId);

      this._transport
        ?.send({
          jsonrpc: "2.0",
          method: "notifications/cancelled",
          params: {
            requestId: messageId,
            reason: String(reason),
          },
        })
        .catch((error) =>
          this._onerror(new Error(`Failed to send cancellation: ${error}`)),
        );

      reject(reason);
    };

    this._responseHandlers.set(messageId, (response) => {
      if (options?.signal?.aborted) {
        return;
      }

      if (response instanceof Error) {
        return reject(response);
      }

      try {
        const result = resultSchema.parse(response.result);
        resolve(result);
      } catch (error) {
        reject(error);
      }
    });

    options?.signal?.addEventListener("abort", () => {
      cancel(options?.signal?.reason);
    });

    const timeout = options?.timeout ?? DEFAULT_REQUEST_TIMEOUT_MSEC;
    const timeoutHandler = () => cancel(new McpError(
      ErrorCode.RequestTimeout,
      "Request timed out",
      { timeout }
    ));

    this._setupTimeout(messageId, timeout, options?.maxTotalTimeout, timeoutHandler);

    this._transport.send(jsonrpcRequest).catch((error) => {
      this._cleanupTimeout(messageId);
      reject(error);
    });
  });
}
```

