TITLE: Testing Token Refresh Handling in SSE Transport
DESCRIPTION: Test case that verifies the SSE client transport can automatically refresh expired tokens and continue operation with the new tokens.
SOURCE: https://github.com/eyaltoledano/claude-task-master/blob/main/context/mcp-js-sdk-docs.txt#2025-04-23_snippet_38

LANGUAGE: typescript
CODE:
```
it("handles token refresh on 401", async () => {
  // Mock tokens() to return expired token until saveTokens is called
  let currentTokens: OAuthTokens = {
    access_token: "expired-token",
    token_type: "Bearer",
    refresh_token: "refresh-token"
  };
  mockAuthProvider.tokens.mockImplementation(() => currentTokens);
  mockAuthProvider.saveTokens.mockImplementation((tokens) => {
    currentTokens = tokens;
  });

  // Create server that accepts SSE but returns 401 on POST with expired token
  await server.close();

  let postAttempts = 0;
  server = createServer((req, res) => {
    lastServerRequest = req;

    if (req.url === "/token" && req.method === "POST") {
      // Handle token refresh request
      let body = "";
      req.on("data", chunk => { body += chunk; });
      req.on("end", () => {
        const params = new URLSearchParams(body);
        if (params.get("grant_type") === "refresh_token" &&
          params.get("refresh_token") === "refresh-token" &&
          params.get("client_id") === "test-client-id" &&
          params.get("client_secret") === "test-client-secret") {
          res.writeHead(200, { "Content-Type": "application/json" });
          res.end(JSON.stringify({
            access_token: "new-token",
            token_type: "Bearer",
            refresh_token: "new-refresh-token"
          }));
        } else {
          res.writeHead(400).end();
        }
      });
      return;
    }

    switch (req.method) {
      case "GET":
        if (req.url !== "/") {
          res.writeHead(404).end();
          return;
        }

        res.writeHead(200, {
          "Content-Type": "text/event-stream",
          "Cache-Control": "no-cache",
          Connection: "keep-alive",
        });
        res.write("event: endpoint\n");
        res.write(`data: ${baseUrl.href}\n\n`);
        break;

      case "POST": {
        if (req.url !== "/") {
          res.writeHead(404).end();
          return;
        }

        const auth = req.headers.authorization;
        if (auth === "Bearer expired-token") {
          res.writeHead(401).end();
          return;
        }

        if (auth === "Bearer new-token") {
          res.writeHead(200).end();
          postAttempts++;
          return;
        }

        res.writeHead(401).end();
        break;
      }
    }
  });

  await new Promise<void>(resolve => {
    server.listen(0, "127.0.0.1", () => {
      const addr = server.address() as AddressInfo;
      baseUrl = new URL(`http://127.0.0.1:${addr.port}`);
      resolve();
    });
  });

  transport = new SSEClientTransport(baseUrl, {
    authProvider: mockAuthProvider,
  });

  await transport.start();

  const message: JSONRPCMessage = {
    jsonrpc: "2.0",
    id: "1",
    method: "test",
    params: {},
  };

  await transport.send(message);

  expect(mockAuthProvider.saveTokens).toHaveBeenCalledWith({
    access_token: "new-token",
    token_type: "Bearer",
    refresh_token: "new-refresh-token"
  });
  expect(postAttempts).toBe(1);
  expect(lastServerRequest.headers.authorization).toBe("Bearer new-token");
});
```

